//Each piece of UI will be bound to it's own Vue JS instance. This allows for reactive
//property changes via directives declared inside html tags and binding annotations i.e.
//handlebar syntax: {{propertyName}}

//Community cards and pot 
var community = new Vue({
	el: '#community',

	data: {
	   	potSize: 0,
		flop1Url: 'http://i.imgur.com/uCi6EeO.png',
		flop2Url: 'http://i.imgur.com/otFrf7M.png',
		flop3Url: 'http://i.imgur.com/fuF6DXz.png',
		turnUrl: 'http://i.imgur.com/llR3t2n.png',
		riverUrl: 'http://i.imgur.com/TFYPQyb.png'
	}

})

//HUD 1
var hud1 = new Vue({	
	el: '#hud1',      //binds Vue instance to div with id "hud1"

	data: {
		isTurn: false,
		timeout: false,
		username: "username",
		card1Url: "http://i.imgur.com/TFYPQyb.png",
		card2Url: 'http://i.imgur.com/55JwrT2.png',
		avatarUrl: 'data:image/jpeg;base64,',
		currency: 0.00,
		timer: 0
	},

	methods: {
		startClock: function(){
						this.timer = 30;
						var that = this;
						var clock = setInterval(countdown, 1000);
						function countdown (){
							that.timer--; 
							if(that.timer == 0){
								clearInterval(clock);
							}
						}
		}
	}
})

